mapper.select:
SELECT o.*, b.`name` AS bookName, b.`writer` AS writerName
FROM `orderlist` o
         JOIN `book` b ON o.bookId = b.bkid
WHERE o.`memberId` = #{memberId}
ORDER BY `orderDate` DESC;

SELECT o.*, b.`name` AS bookName, b.`writer` AS writerName
FROM `orderlist` o
         JOIN `book` b ON o.bookId = b.bkid
WHERE o.`memberId` = #{memberId}
ORDER BY b.`name` ASC;
